import { Box, Tab, Tabs, Typography } from "@mui/material";
import axios from "axios";
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel';
import { useEffect, useState } from "react";

export default function TabFilterComponent() {
  const genreApi = "https://qtify-backend-labs.crio.do/genres";

  const [data, setData] = useState();

  let fetchData = async () => {
    try {
      let response = await axios.get(genreApi);
      console.log(response.data.data);
      setData(response.data.data);
    } catch (err) {
      console.error("error while fetching top albums", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const [selectedTab, setSelectedTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };
  return (
    <div>
      <Typography style={{ color: "#121212" }}>Songs</Typography>

      <ul className="nav nav-underline">
        <li className="nav-item" key="all" role="presentation">
          <Typography className="nav-link active" aria-current="page" href="#">
            All
          </Typography>
        </li>
        {data
          ? data.map((e) => (
              <li className="nav-item" key={e.key} role="presentation">
                <Typography className="nav-link " aria-current="page" href="#">
                  {e.label}
                </Typography>
              </li>
            ))
          : "loading..."}
      </ul>
      
     
   
    </div>
  );
}
