import { Box, Tab, Tabs, Typography } from "@mui/material";
import axios from "axios";
import { useEffect, useState } from "react";

export default function TabFilterComponent() {
  const genreApi = "https://qtify-backend-labs.crio.do/genres";
const songApi ='https://qtify-backend-labs.crio.do/songs';

  
  const [genre, setGenre] = useState();
  const [data, setData] = useState();

  let fetchData = async () => {
    try {
      let responseGenre = await axios.get(genreApi);
      let response = await axios.get(songApi);
      console.log(responseGenre.data.data);
      console.log(response.data);
      setGenre(responseGenre.data.data);
    } catch (err) {
      console.error("error while fetching top albums", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const [selectedTab, setSelectedTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };
  return (
    <div style={{ backgroundColor: "#121212", color: "#fff" }}>
      <div style={{ margin: "0 1rem" }}>
        <Typography
          style={{ color: "#fff", display: "flex", justifyContent: "left" }}
        >
          Songs
        </Typography>

        <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
          <Tabs
            value={selectedTab}
            onChange={handleTabChange}
            textColor='#fff'
          >
            <Tab label="All" />

            {genre
              ? genre.map((e) => <Tab key={e.key} label={e.label} />)
              : "loading..."}
          </Tabs>
        </Box>
        {selectedTab === 0 && <div>Tab 1 Content</div>}
        {selectedTab === 1 && <div>Tab 2 Content</div>}
        {selectedTab === 2 && <div>Tab 3 Content</div>}
        {selectedTab === 3 && <div>Tab 4 Content</div>}
        {selectedTab === 4 && <div>Tab 5 Content</div>}
      </div>
    </div>
  );
}
