import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Typography,
} from "@mui/material";
import { useState } from "react";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import "./FAQ.css";

export default function FAQ() {
  const [expanded, setExpanded] = useState(false);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  return (
    <div  style={{ backgroundColor: "#121212", color: "#fff" }}>
      <div
    
        style={{ backgroundColor: "#121212", color: "#fff", margin:'0 15rem' }}
      >
        <Typography>FAQs</Typography>

        <Accordion
          expanded={expanded === "panel1"}
          onChange={handleChange("panel1")}
           style={{borderColor:'#fff'}}
        >
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1bh-content"
            style={{ backgroundColor: "#121212", color: "#fff" ,borderColor:'#fff'}}
          >
            Is Qtify free to use?
          </AccordionSummary>
          <AccordionDetails
            style={{ backgroundColor: "#121212", color: "#fff" }}
          >
            <Typography style={{ textAlign: "left" }}>
              Nulla facilisi. Phasellus sollicitudin nulla et quam mattis
              feugiat. Aliquam eget maximus est, id dignissim quam.
            </Typography>
          </AccordionDetails>
        </Accordion>

        <Accordion
          expanded={expanded === "panel2"}
          onChange={handleChange("panel2")}
        >
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1bh-content"
            style={{ backgroundColor: "#121212", color: "#fff" }}
          >
            Can i download & listen to songs online?
          </AccordionSummary>
          <AccordionDetails
            className="accordian_typo"
            style={{ backgroundColor: "#121212", color: "#fff" }}
          >
            <Typography style={{ textAlign: "left" }}>Yes</Typography>
          </AccordionDetails>
        </Accordion>

        <Accordion
          expanded={expanded === "panel3"}
          onChange={handleChange("panel3")}
        >
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1bh-content"
            style={{ backgroundColor: "#121212", color: "#fff" }}
          >
            Is Qtify free to use?
          </AccordionSummary>
          <AccordionDetails
            style={{ backgroundColor: "#121212", color: "#fff" }}
          >
            <Typography style={{ textAlign: "left" }}>
              Nulla facilisi. Phasellus sollicitudin nulla et quam mattis
              feugiat. Aliquam eget maximus est, id dignissim quam.
            </Typography>
          </AccordionDetails>
        </Accordion>
      </div>
    </div>
  );
}
