import { Button, Typography } from "@mui/material";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation } from "swiper/modules";
import axios from "axios";
import { useEffect, useState } from "react";
import CardComponent from "./CardComponent";

export default function TopAlbum() {
  const topAlbumAPI = "https://qtify-backend-labs.crio.do/albums/top";
  const [data, setData] = useState();

  Swiper.use([Navigation]);

  let fetchData = async () => {
    try {
      let response = await axios.get(topAlbumAPI);
      console.log(response.data);
      setData(response.data);
    } catch (err) {
      console.error("error while fetching top albums", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div style={{ backgroundColor: "#121212", color: "#fff" }}>
      <div style={{ margin: "0 1rem" }}>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <Typography>Top Albums</Typography>
          <Button style={{ textTransform: "none", color: "#34c84b" }}>
            Show All
          </Button>
        </div>
        <Swiper
        modules={[Navigation]}
          slidesPerView={6}
          onSlideChange={() => console.log("slide change")}
          onSwiper={(swiper) => console.log(swiper)}
          navigation
        >
          {data
            ? data.map((e) => {
                return (
                  <div key={e.id}>
                    <SwiperSlide>
                      <CardComponent
                        image={e.image}
                        followers={e.follows}
                        title={e.title}
                        ifSong={false}
                      />
                    </SwiperSlide>
                  </div>
                );
              })
            : "loading..."}
        </Swiper>
      </div>
    </div>
  );
}
