import "./Navbar.css";
import Logo from "../assets/Logo.png";
import { Button, IconButton, TextField } from "@material-ui/core";
import SearchIcon from "@mui/icons-material/Search";
import Autocomplete from "@mui/material/Autocomplete";
import axios from "axios";
import { useEffect, useState } from "react";
import { Typography } from "@mui/material";

export default function Navbar() {
  const albumApi = "https://qtify-backend-labs.crio.do/songs";

  const [data, setData] = useState([]);

  const fetchData = async () => {
    try {
      const responseSongs = await axios.get(albumApi);
      setData(responseSongs.data);
    } catch (err) {
      console.error("Error while fetching data", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="navbar">
      <div className="navabar_content">
        <img src={Logo} alt="logo" />
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Autocomplete
            freeSolo
            variant="outlined"
            size="small"
            placeholder="Search a song of your choice"
            style={{ width: "23rem" }}
            options={data}
            renderOption={(props, option) => (
              <div
                key={option.id}
                style={{
                  display: "flex",
                  justifyContent: "center",
                  backgroundColor: "#121212",
                  color: "#fff",
                }}
              >
                <img
                  loading="lazy"
                  width="20"
                  src={option.image}
                  alt={props.title}
                />
                <Typography>{option.title}</Typography>
                <Typography>{option.follows} follows</Typography>
              </div>
            )}
            renderInput={(params) => (
              <TextField {...params} label="Search a song of your choice" />
            )}
          />
          {/* <TextField
            variant="outlined"
            size="small"
            placeholder="Search a song of your choice"
            style={{ width: "20rem" }}
          /> */}

          <IconButton variant="outlined">
            <SearchIcon />
          </IconButton>
        </div>

        <Button
          variant="contained"
          style={{
            textTransform: "none",
            backgroundColor: "#121212",
            color: "#34C94B",
          }}
          size="small"
        >
          Give FeedBack
        </Button>
      </div>
    </div>
  );
}
