import { Button, Typography } from "@mui/material";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation } from "swiper/modules";
import "./TopAlbum.css";
import axios from "axios";
import { useEffect, useState } from "react";
import CardComponent from "./CardComponent";

export default function TopAlbum() {
  const topAlbumAPI = "https://qtify-backend-labs.crio.do/albums/top";
  const [data, setData] = useState();

  let fetchData = async () => {
    try {
      let response = await axios.get(topAlbumAPI);
      console.log(response.data);
      setData(response.data);
    } catch (err) {
      console.error("error while fetching top albums", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const [showAll, setShowAll] = useState(false);

  const showButtonToggle=()=>{
    if(showAll){
        setShowAll(false)
    }else{
        showAll(true);
    }
  }

  return (
    <div style={{ backgroundColor: "#121212", color: "#fff" }}>
      <div style={{ margin: "0 1rem" }}>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <Typography>Top Albums</Typography>
          <Button style={{ textTransform: "none", color: "#34c84b" }} onClick={()=>showButtonToggle()}>
            Show All
          </Button>
        </div>

        {data ? (
          showAll ? (
            <div className="row">
              {data
                ? data.map((e) => {
                    return (
                      <div className="col-2" key={e.id}>
                        <CardComponent
                          image={e.image}
                          followers={e.follows}
                          title={e.title}
                          ifSong={false}
                        />
                      </div>
                    );
                  })
                : "loading"}
            </div>
          ) : (
            <Swiper
              modules={[Navigation]}
              slidesPerView={6}
              onSlideChange={() => console.log("slide change")}
              onSwiper={(swiper) => console.log(swiper)}
              navigation
              swiper-button-size="20px"
            >
              {data
                ? data.map((e) => {
                    return (
                      <div key={e.id}>
                        <SwiperSlide>
                          <CardComponent
                            image={e.image}
                            followers={e.follows}
                            title={e.title}
                            ifSong={false}
                          />
                        </SwiperSlide>
                      </div>
                    );
                  })
                : "loading..."}
            </Swiper>
          )
        ) : (
          "loading..."
        )}
      </div>
    </div>
  );
}
